import React, { useState, useEffect, useRef } from 'react';
import {
  Box,
  Drawer,
  Typography,
  Paper,
  TextField,
  Button,
  Avatar,
  Divider,
  Chip,
  Fab,
  useMediaQuery,
  useTheme,
  CircularProgress,
  Alert,
  Snackbar,
  CssBaseline,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Switch,
  FormControlLabel
} from '@mui/material';
import {
  Send,
  Person,
  Description,
  TableChart,
  Save
} from '@mui/icons-material';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import ChatHistory from './components/ChatHistory';
import './App.css';

// API URL configuration
const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';

const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: { main: '#8B5CF6' },
    secondary: { main: '#6366F1' },
    background: { default: '#000000', paper: '#0A0A0A' },
    text: { primary: '#FFFFFF', secondary: '#E5E7EB' },
    success: { main: '#10B981' },
    error: { main: '#EF4444' }
  },
  typography: {
    fontFamily: '"Inter", "Roboto", "Helvetica", "Arial", sans-serif',
    h4: { fontWeight: 700 },
    h6: { fontWeight: 600 }
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: { borderRadius: 12, textTransform: 'none', fontWeight: 600, padding: '10px 20px' },
        contained: {
          boxShadow: '0 4px 20px rgba(139, 92, 246, 0.4)',
          background: 'linear-gradient(135deg, #8B5CF6 0%, #6366F1 100%)',
          '&:hover': {
            transform: 'translateY(-1px)',
            boxShadow: '0 6px 30px rgba(139, 92, 246, 0.6)',
            background: 'linear-gradient(135deg, #A78BFA 0%, #818CF8 100%)',
          }
        },
      },
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          background: 'rgba(20, 20, 20, 0.95)',
          backdropFilter: 'blur(20px)',
          border: '1px solid rgba(139, 92, 246, 0.3)',
          borderRadius: 16,
        },
      },
    },
  },
});

const drawerWidth = 280;

function App() {
  const muiTheme = useTheme();
  const isMobile = useMediaQuery(muiTheme.breakpoints.down('md'));
  
  const [messages, setMessages] = useState([
    { text: '◊©◊ú◊ï◊ù! ◊ê◊†◊ô TLV500 AI Assistant. ◊î◊™◊ó◊ë◊® ◊ú◊ó◊©◊ë◊ï◊ü Google ◊ï◊ë◊ó◊® ◊í◊ô◊ú◊ô◊ï◊ü ◊õ◊ì◊ô ◊ú◊î◊™◊ó◊ô◊ú.', sender: 'assistant', timestamp: new Date(), id: Date.now() }
  ]);
  const [question, setQuestion] = useState('');
  const [fileName, setFileName] = useState('');
  const [googleSheetsUrl, setGoogleSheetsUrl] = useState('');
  const [changesCount, setChangesCount] = useState(0);
  const [isSheetLoaded, setIsSheetLoaded] = useState(false);

  const [isPickerReady, setIsPickerReady] = useState(false);
  const [accessToken, setAccessToken] = useState('');
  const [sheetsMetadata, setSheetsMetadata] = useState('');
  const [authError, setAuthError] = useState('');
  const [showSnackbar, setShowSnackbar] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState('');
  const [availableSheets, setAvailableSheets] = useState([]);
  const [selectedSheetName, setSelectedSheetName] = useState('');
  const [isLoadingSheets, setIsLoadingSheets] = useState(false);
  const [extractedPdf, setExtractedPdf] = useState(null);
  const [isExtractingData, setIsExtractingData] = useState(false);
  const [extractedExcel, setExtractedExcel] = useState(null);
  const [isExtractingExcel, setIsExtractingExcel] = useState(false);
  const [isAgentMode, setIsAgentMode] = useState(false);
  const [isStreamingResponse, setIsStreamingResponse] = useState(false);
  const [isSheetSelected, setIsSheetSelected] = useState(false);
  const [isIframeHovered, setIsIframeHovered] = useState(false);
  
  // üîÑ Undo/Redo State
  const [canUndo, setCanUndo] = useState(false);
  const [canRedo, setCanRedo] = useState(false);
  const [hasGreenCells, setHasGreenCells] = useState(false);
  const [isCheckingGreenCells, setIsCheckingGreenCells] = useState(false);
  
  // Chat History Management
  const [currentSessionId, setCurrentSessionId] = useState(null);
  const [user, setUser] = useState(null);
  
  const chatEndRef = useRef(null);
  const iframeContainerRef = useRef(null);


  useEffect(() => {
    // Load saved access token ONLY - no chat messages from localStorage
    const savedToken = localStorage.getItem('accessToken');
    if (savedToken) {
      setAccessToken(savedToken);
    }

    // Only load PDF/Excel data from localStorage (temporary files, not chat history)
    const savedPdfJson = localStorage.getItem('extractedPdf');
    if (savedPdfJson) {
      try {
        const savedPdf = JSON.parse(savedPdfJson);
        if (savedPdf && savedPdf.name && savedPdf.data) {
          setExtractedPdf(savedPdf);
          setMessages(prev => [...prev, { text: `üìÑ ◊†◊™◊ï◊†◊ô◊ù ◊ß◊ï◊ì◊û◊ô◊ù ◊©◊ó◊ï◊ú◊¶◊ï ◊û-PDF "${savedPdf.name}" ◊†◊ò◊¢◊†◊ï.`, sender: 'system', timestamp: new Date(), id: Date.now() }]);
        }
      } catch (e) {
        console.error('Failed to parse extracted PDF data from localStorage', e);
        localStorage.removeItem('extractedPdf');
      }
    }

    const savedExcelJson = localStorage.getItem('extractedExcel');
    if (savedExcelJson) {
        try {
            const savedExcel = JSON.parse(savedExcelJson);
            if (savedExcel && savedExcel.name && savedExcel.data) {
                setExtractedExcel(savedExcel);
                setMessages(prev => [...prev, { text: `üìä ◊†◊™◊ï◊†◊ô◊ù ◊ß◊ï◊ì◊û◊ô◊ù ◊©◊ó◊ï◊ú◊¶◊ï ◊û-Excel "${savedExcel.name}" ◊†◊ò◊¢◊†◊ï.`, sender: 'system', timestamp: new Date(), id: Date.now() }]);
            }
        } catch (e) {
            console.error('Failed to parse extracted Excel data from localStorage', e);
            localStorage.removeItem('extractedExcel');
        }
    }
  }, []);

  // Fetch user info when access token is available
  useEffect(() => {
    if (accessToken) {
      fetchUserInfo();
    }
  }, [accessToken]);

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const token = params.get('token');
    if (token) {
      setAccessToken(token);
      localStorage.setItem('accessToken', token);
      setSnackbarMessage('◊î◊™◊ó◊ë◊®◊™ ◊ë◊î◊¶◊ú◊ó◊î!');
      setShowSnackbar(true);
      window.history.replaceState({}, document.title, "/");
    } else {
      // Check if user is already authenticated
      checkAuthStatus();
    }
  }, []);

  const checkAuthStatus = async () => {
    try {
      const response = await fetch(`${API_URL}/api/user`, {
        credentials: 'include'
      });
      
      if (response.ok) {
        const data = await response.json();
        setUser(data.user);
        if (data.accessToken) {
          setAccessToken(data.accessToken);
          localStorage.setItem('accessToken', data.accessToken);
        }
        setSnackbarMessage('◊ë◊®◊ï◊ö ◊î◊ë◊ê ◊ë◊ó◊ñ◊®◊î!');
        setShowSnackbar(true);
      }
    } catch (error) {
      console.log('Not authenticated:', error);
      // User is not authenticated, show login button
    }
  };

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  useEffect(() => {
    const script = document.createElement('script');
    script.src = 'https://apis.google.com/js/api.js';
    script.onload = () => window.gapi.load('picker', { 'callback': () => setIsPickerReady(true) });
    document.head.appendChild(script);
  }, []);

  // ◊†◊ô◊î◊ï◊ú ◊í◊ú◊ô◊ú◊î ◊¢◊ë◊ï◊® iframe - ◊î◊í◊†◊î ◊û◊ô◊ï◊ó◊ì◊™ ◊¢◊ú Mac gestures
  useEffect(() => {
    const handleWheel = (e) => {
      if (isIframeHovered) {
        // ◊î◊í◊†◊î ◊°◊§◊¶◊ô◊§◊ô◊™ ◊¢◊ú horizontal scrolling ◊ë◊û◊ê◊ß
        if (Math.abs(e.deltaX) > Math.abs(e.deltaY)) {
          // ◊ñ◊î ◊í◊ú◊ô◊ú◊î ◊ê◊ï◊§◊ß◊ô◊™ - ◊ë◊ú◊ï◊ß ◊ê◊™ ◊înavigation gesture
          e.preventDefault();
          e.stopPropagation();
        }
      }
    };

    const handleTouchStart = (e) => {
      if (isIframeHovered) {
        e.stopPropagation();
      }
    };

    const handleTouchMove = (e) => {
      if (isIframeHovered) {
        e.stopPropagation();
      }
    };

    // ◊î◊ï◊°◊£ event listeners ◊¢◊ù focus ◊¢◊ú gestures
    document.addEventListener('wheel', handleWheel, { passive: false });
    document.addEventListener('touchstart', handleTouchStart, { passive: false });
    document.addEventListener('touchmove', handleTouchMove, { passive: false });

    return () => {
      document.removeEventListener('wheel', handleWheel);
      document.removeEventListener('touchstart', handleTouchStart);
      document.removeEventListener('touchmove', handleTouchMove);
    };
  }, [isIframeHovered]);

  // ◊ë◊ì◊ô◊ß◊™ ◊°◊ò◊ò◊ï◊° ◊ê◊ï◊ò◊ï◊û◊ò◊ô◊™ ◊õ◊©◊í◊ú◊ô◊ï◊ü ◊†◊ë◊ó◊®
  useEffect(() => {
    if (isSheetSelected && selectedSheetName && currentSessionId) {
      // Auto-check status when sheet is selected
      // ◊ë◊ì◊ô◊ß◊î ◊ê◊ï◊ò◊ï◊û◊ò◊ô◊™ ◊ê◊ó◊®◊ô ◊ò◊¢◊ô◊†◊™ ◊î◊í◊ú◊ô◊ï◊ü
      const timer = setTimeout(() => {
        checkUndoRedoStatus();
        checkForGreenCells();
      }, 2000);
      
      return () => clearTimeout(timer);
    }
  }, [selectedSheetName, isSheetSelected, currentSessionId]);

  const fetchUserInfo = async () => {
    try {
      const response = await fetch(`${API_URL}/api/user`, {
        credentials: 'include'
      });
      
      if (response.ok) {
        const data = await response.json();
        setUser(data.user);
      }
    } catch (error) {
      console.error('Error fetching user info:', error);
    }
  };

  const handleSessionSelect = async (sessionId) => {
    if (!sessionId) {
      setCurrentSessionId(null);
      setMessages([{ text: '◊©◊ú◊ï◊ù! ◊ê◊†◊ô TLV500 AI Assistant. ◊î◊™◊ó◊ë◊® ◊ú◊ó◊©◊ë◊ï◊ü Google ◊ï◊ë◊ó◊® ◊í◊ô◊ú◊ô◊ï◊ü ◊õ◊ì◊ô ◊ú◊î◊™◊ó◊ô◊ú.', sender: 'assistant', timestamp: new Date(), id: Date.now() }]);
      return;
    }

    setCurrentSessionId(sessionId);
    
    try {
      const response = await fetch(`${API_URL}/api/chat/session/${sessionId}`, {
        credentials: 'include'
      });
      
      if (response.ok) {
        const chatHistory = await response.json();
        const loadedMessages = chatHistory.messages.map(msg => ({
          text: msg.content,
          sender: msg.role === 'user' ? 'user' : 'assistant',
          timestamp: new Date(msg.timestamp),
          id: msg._id || Date.now() + Math.random()
        }));
        
        setMessages(loadedMessages);
        // Chat history loaded
      }
    } catch (error) {
      console.error('Error loading chat session:', error);
      setSnackbarMessage('◊©◊í◊ô◊ê◊î ◊ë◊ò◊¢◊ô◊†◊™ ◊î◊©◊ô◊ó◊î');
      setShowSnackbar(true);
    }
  };

  const generateSessionId = () => {
    return 'session_' + Date.now() + '_' + Math.random().toString(36).substr(2, 9);
  };




  
  const makeGoogleSheetsAPICall = async (spreadsheetId, range, values = null, method = 'GET') => {
    if (!accessToken) throw new Error('No access token available. Please log in.');
    const baseUrl = `https://sheets.googleapis.com/v4/spreadsheets/${spreadsheetId}`;
    try {
      let url, options;
      if (method === 'GET') {
        url = `${baseUrl}/values/${range}`;
        options = { method, headers: { 'Authorization': `Bearer ${accessToken}`, 'Content-Type': 'application/json' }};
      } else {
        url = `${baseUrl}/values/${range}?valueInputOption=RAW`;
        options = { method, headers: { 'Authorization': `Bearer ${accessToken}`, 'Content-Type': 'application/json' }, body: JSON.stringify({ values }) };
      }
      const response = await fetch(url, options);
      const data = await response.json();
      if (!response.ok) throw new Error(data.error?.message || 'API call failed');
      return data;
    } catch (error) {
      console.error('Google Sheets API error:', error);
      throw error;
    }
  };

  const extractSpreadsheetId = (url) => {
    const match = url.match(/\/spreadsheets\/d\/([a-zA-Z0-9-_]+)/);
    return match ? match[1] : null;
  };
  
  // ◊§◊ï◊†◊ß◊¶◊ô◊î ◊ú◊ô◊¶◊ï◊® URL ◊¢◊ù ◊î◊í◊ú◊ô◊ï◊ü ◊î◊†◊õ◊ï◊ü
  const getSheetUrlWithGid = (baseUrl, sheetName) => {
    if (!sheetName || !sheetsMetadata) return baseUrl;
    
    const selectedSheet = sheetsMetadata.find(sheet => sheet.name === sheetName);
    if (!selectedSheet) return baseUrl;
    
    // ◊î◊°◊® ◊õ◊ú gid ◊ß◊ô◊ô◊ù ◊û◊î-URL
    const cleanUrl = baseUrl.split('#')[0];
    
    // ◊î◊ï◊°◊£ ◊ê◊™ ◊î-gid ◊î◊ó◊ì◊©
    return `${cleanUrl}#gid=${selectedSheet.id}`;
  };

  const getSpreadsheetMetadata = async (spreadsheetId) => {
    if (!accessToken) return [];
    try {
      const url = `https://sheets.googleapis.com/v4/spreadsheets/${spreadsheetId}`;
      const response = await fetch(url, { headers: { 'Authorization': `Bearer ${accessToken}` } });
      if (!response.ok) throw new Error('Failed to fetch spreadsheet metadata');
      const metadata = await response.json();
      return metadata.sheets.map(sheet => ({ name: sheet.properties.title, id: sheet.properties.sheetId }));
    } catch (error) {
      console.error('Error fetching spreadsheet metadata:', error);
      return [];
    }
  };

  const handleFileUpload = async (event, fileType) => {
    const file = event.target.files[0];
    if (!file) return;

    if (file.type === 'application/pdf') {
        // Clean all previous PDF-related state
        setExtractedPdf(null);
        localStorage.removeItem('extractedPdf');
        setIsExtractingData(true);
        
        // Remove any previous PDF-related messages
        setMessages(prev => prev.filter(msg => 
            !msg.text.includes('◊†◊™◊ï◊†◊ô◊ù ◊ó◊ï◊ú◊¶◊ï') && 
            !msg.text.includes('◊©◊í◊ô◊ê◊î ◊ë◊ó◊ô◊ú◊ï◊•') &&
            !msg.text.includes('◊†◊™◊ï◊†◊ô ◊î-PDF ◊†◊ï◊ß◊ï')
        ));
        
        setMessages(prev => [...prev, { text: `‚è≥ ◊û◊ó◊ú◊• ◊†◊™◊ï◊†◊ô◊ù ◊û◊ß◊ï◊ë◊• "${file.name}"...`, sender: 'system', timestamp: new Date(), id: Date.now() }]);
        
        const formData = new FormData();
        formData.append('pdf', file);

        fetch(`${API_URL}/api/extract-pdf-data`, {
            method: 'POST', 
            body: formData 
        })
        .then(response => {
            if (!response.ok) {
                return response.json().then(err => { throw new Error(err.error || 'Failed to extract data') });
            }
            return response.json();
        })
        .then(result => {
            const pdfInfo = { name: file.name, data: result };
            setExtractedPdf(pdfInfo);
            localStorage.setItem('extractedPdf', JSON.stringify(pdfInfo));
            setMessages(prev => prev.filter(msg => !msg.text.includes('‚è≥ ◊û◊ó◊ú◊• ◊†◊™◊ï◊†◊ô◊ù')));
            setMessages(prev => [...prev, { text: `‚úÖ ◊†◊™◊ï◊†◊ô◊ù ◊ó◊ï◊ú◊¶◊ï ◊ë◊î◊¶◊ú◊ó◊î ◊û-"${file.name}". ◊õ◊¢◊™ ◊†◊ô◊™◊ü ◊ú◊©◊ê◊ï◊ú ◊©◊ê◊ú◊ï◊™ ◊¢◊ú ◊î◊ß◊ï◊ë◊•.`, sender: 'system', timestamp: new Date(), id: Date.now() }]);
        })
        .catch(error => {
            console.error('PDF data extraction failed:', error);
            setMessages(prev => prev.filter(msg => !msg.text.includes('‚è≥ ◊û◊ó◊ú◊• ◊†◊™◊ï◊†◊ô◊ù')));
            setMessages(prev => [...prev, { text: `‚ùå ◊©◊í◊ô◊ê◊î ◊ë◊ó◊ô◊ú◊ï◊• ◊†◊™◊ï◊†◊ô◊ù ◊û-"${file.name}": ${error.message}`, sender: 'system', timestamp: new Date(), id: Date.now() }]);
        })
        .finally(() => {
            setIsExtractingData(false);
        });
    } else if (fileType === 'Excel') {
        setExtractedExcel(null);
        localStorage.removeItem('extractedExcel');
        setIsExtractingExcel(true);
        
        setMessages(prev => prev.filter(msg => 
            !msg.text.includes('◊ó◊ï◊ú◊¶◊ï ◊û-Excel') && 
            !msg.text.includes('◊©◊í◊ô◊ê◊î ◊ë◊ó◊ô◊ú◊ï◊• Excel') &&
            !msg.text.includes('◊†◊™◊ï◊†◊ô ◊î-Excel ◊†◊ï◊ß◊ï')
        ));
        
        setMessages(prev => [...prev, { text: `‚è≥ ◊û◊ó◊ú◊• ◊†◊™◊ï◊†◊ô◊ù ◊û◊ß◊ï◊ë◊• Excel "${file.name}"...`, sender: 'system', timestamp: new Date(), id: Date.now() }]);
        
        const formData = new FormData();
        formData.append('excel', file);

        fetch(`${API_URL}/api/extract-excel-data`, {
            method: 'POST', 
            body: formData 
        })
        .then(response => {
            if (!response.ok) {
                return response.json().then(err => { throw new Error(err.error || 'Failed to extract Excel data') });
            }
            return response.json();
        })
        .then(result => {
            const excelInfo = { name: file.name, data: result.data };
            setExtractedExcel(excelInfo);
            localStorage.setItem('extractedExcel', JSON.stringify(excelInfo));
            setMessages(prev => prev.filter(msg => !msg.text.includes('‚è≥ ◊û◊ó◊ú◊• ◊†◊™◊ï◊†◊ô◊ù ◊û◊ß◊ï◊ë◊• Excel')));
            setMessages(prev => [...prev, { text: `‚úÖ ◊†◊™◊ï◊†◊ô◊ù ◊ó◊ï◊ú◊¶◊ï ◊ë◊î◊¶◊ú◊ó◊î ◊û-Excel "${file.name}".`, sender: 'system', timestamp: new Date(), id: Date.now() }]);
        })
        .catch(error => {
            console.error('Excel data extraction failed:', error);
            setMessages(prev => prev.filter(msg => !msg.text.includes('‚è≥ ◊û◊ó◊ú◊• ◊†◊™◊ï◊†◊ô◊ù ◊û◊ß◊ï◊ë◊• Excel')));
            setMessages(prev => [...prev, { text: `‚ùå ◊©◊í◊ô◊ê◊î ◊ë◊ó◊ô◊ú◊ï◊• ◊†◊™◊ï◊†◊ô Excel: ${error.message}`, sender: 'system', timestamp: new Date(), id: Date.now() }]);
        })
        .finally(() => {
            setIsExtractingExcel(false);
        });
    }
  };

  const loadAvailableSheets = async (spreadsheetId) => {
    setIsLoadingSheets(true);
    setIsSheetSelected(false); // Reset selection
    setSelectedSheetName(''); // Clear any previous selection
    try {
      const sheets = await getSpreadsheetMetadata(spreadsheetId);
      setAvailableSheets(sheets);
      setSheetsMetadata(sheets);
      // ◊®◊ß ◊î◊ï◊ì◊¢◊î ◊©◊î◊í◊ú◊ô◊ï◊†◊ï◊™ ◊†◊ò◊¢◊†◊ï - ◊ë◊ú◊ô ◊ú◊ë◊ó◊ï◊® ◊í◊ú◊ô◊ï◊ü ◊ê◊ï◊ò◊ï◊û◊ò◊ô◊™
      if (sheets.length > 0) {
        setMessages(prev => [...prev, { 
          text: `üìã ◊†◊û◊¶◊ê◊ï ${sheets.length} ◊í◊ú◊ô◊ï◊†◊ï◊™ - ◊ë◊ó◊® ◊í◊ú◊ô◊ï◊ü ◊õ◊ì◊ô ◊ú◊î◊™◊ó◊ô◊ú`, 
          sender: 'system', 
          timestamp: new Date(), 
          id: Date.now() 
        }]);
      }
    } finally {
      setIsLoadingSheets(false);
    }
  };

  const handleGoogleSheetsUrl = async (url) => {
    setGoogleSheetsUrl(url);
    setIsSheetLoaded(true);
    setIsSheetSelected(false); // Reset sheet selection for new spreadsheet
    setIsAgentMode(false); // Reset agent mode for new spreadsheet
    const spreadsheetId = extractSpreadsheetId(url);
    if (spreadsheetId && accessToken) await loadAvailableSheets(spreadsheetId);
  };
  
  const handleSheetChange = async (newSheetName) => {
    if (newSheetName === selectedSheetName) return;
    
    setSelectedSheetName(newSheetName);
    setIsSheetSelected(true); // ◊û◊°◊û◊ü ◊©◊†◊ë◊ó◊® ◊í◊ú◊ô◊ï◊ü
    
    const spreadsheetId = extractSpreadsheetId(googleSheetsUrl);
    if (spreadsheetId) {
      await getSheetData(spreadsheetId, newSheetName);
      setMessages(prev => [...prev, { 
        text: `‚úÖ ◊û◊ó◊ï◊ë◊® ◊ú◊í◊ú◊ô◊ï◊ü: ${newSheetName}`, 
        sender: 'system', 
        timestamp: new Date(), 
        id: Date.now() 
      }]);
    }
  };

  const pickerCallback = (data) => {
    if (data.action === window.google.picker.Action.PICKED) {
      const doc = data.docs[0];
      setFileName(doc.name);
      handleGoogleSheetsUrl(doc.url);
      setSnackbarMessage(`◊ò◊¢◊ü ◊í◊ô◊ú◊ô◊ï◊ü: ${doc.name}`);
      setShowSnackbar(true);
    }
  };

  const createPicker = () => {
    const developerKey = process.env.REACT_APP_GEMINI_API_KEY;
    const clientId = process.env.REACT_APP_GOOGLE_CLIENT_ID;
    if (!clientId || !developerKey) {
      setAuthError('◊û◊§◊™◊ó◊ï◊™ API ◊ê◊ô◊†◊ù ◊û◊ï◊í◊ì◊®◊ô◊ù ◊õ◊®◊ê◊ï◊ô.');
      setShowSnackbar(true);
      return;
    }
    const view = new window.google.picker.View(window.google.picker.ViewId.SPREADSHEETS);
    view.setMimeTypes("application/vnd.google-apps.spreadsheet");
    const picker = new window.google.picker.PickerBuilder()
      .setAppId(clientId.split('-')[0])
      .setOAuthToken(accessToken)
      .setDeveloperKey(developerKey)
      .addView(view)
      .setCallback(pickerCallback)
      .build();
    picker.setVisible(true);
  };

  const getSheetData = async (spreadsheetId, sheetName = null) => {
    if (!accessToken) return null;
    try {
      const range = sheetName ? `'${sheetName}'!A1:Z1000` : 'A1:Z1000';
      const apiResponse = await makeGoogleSheetsAPICall(spreadsheetId, range);
      // The processSheetData function is removed as we will send raw data to backend
      return apiResponse.values || [];
    } catch (error) {
      console.error('Failed to fetch sheet data:', error);
      setMessages(prev => [...prev, { text: `‚ùå ◊©◊í◊ô◊ê◊î ◊ë◊ò◊¢◊ô◊†◊™ ◊í◊ô◊ú◊ô◊ï◊ü: ${error.message}`, sender: 'system', timestamp: new Date(), id: Date.now() }]);
      return null;
    }
  };

  const startNewChat = () => {
    setCurrentSessionId(null);
    setMessages([{ text: '◊©◊ú◊ï◊ù! ◊ê◊†◊ô TLV500 AI Assistant. ◊î◊™◊ó◊ë◊® ◊ú◊ó◊©◊ë◊ï◊ü Google ◊ï◊ë◊ó◊® ◊í◊ô◊ú◊ô◊ï◊ü ◊õ◊ì◊ô ◊ú◊î◊™◊ó◊ô◊ú.', sender: 'assistant', timestamp: new Date(), id: Date.now() }]);
    // ◊ê◊§◊° ◊û◊¶◊ë Agent Mode ◊ê◊ù ◊ê◊ô◊ü ◊í◊ú◊ô◊ï◊ü ◊†◊ë◊ó◊®
    if (!isSheetSelected) {
      setIsAgentMode(false);
    }
    // ◊ê◊§◊° ◊û◊¶◊ë Undo/Redo
    setCanUndo(false);
    setCanRedo(false);
