            }
          }, 500); // ◊î◊©◊î◊ô◊î ◊ß◊¶◊®◊î ◊ú◊§◊†◊ô ◊®◊¢◊†◊ï◊ü
        }
      } else {
        const error = await response.json();
        setSnackbarMessage('‚ùå ◊©◊í◊ô◊ê◊î ◊ë◊ë◊ô◊ò◊ï◊ú: ' + error.error);
        setShowSnackbar(true);
      }
    } catch (error) {
