      setSnackbarMessage('‚ùå ◊©◊í◊ô◊ê◊î ◊ë◊©◊ó◊ñ◊ï◊® ◊î◊§◊¢◊ï◊ú◊î');
      setShowSnackbar(true);
    }
  };

  const sendMessage = async () => {
    if (!question.trim() || isStreamingResponse) return;

    // üõ°Ô∏è ◊ë◊ì◊ô◊ß◊î ◊ó◊ì◊©◊î - ◊ê◊ù ◊ô◊© ◊í◊ú◊ô◊ï◊ü ◊ê◊ë◊ú ◊ú◊ê ◊†◊ë◊ó◊® ◊í◊ú◊ô◊ï◊ü ◊°◊§◊¶◊ô◊§◊ô
    if (isSheetLoaded && !isSheetSelected) {
      setMessages(prev => [...prev, { 
        text: '‚ùå ◊ô◊© ◊ú◊ë◊ó◊ï◊® ◊í◊ú◊ô◊ï◊ü ◊™◊ó◊ô◊ú◊î ◊û◊î◊®◊©◊ô◊û◊î ◊ú◊û◊¢◊ú◊î', 
        sender: 'system', 
        timestamp: new Date(), 
        id: Date.now() 
      }]);
      return;
    }

    const currentQuestion = question;
    const userMessage = { text: currentQuestion, sender: 'user', timestamp: new Date(), id: Date.now() };
    setMessages(prev => [...prev, userMessage]);
    setQuestion('');
    setIsStreamingResponse(true);
    
    // Generate session ID if this is a new conversation
    let sessionId = currentSessionId;
    if (!sessionId) {
      sessionId = generateSessionId();
      setCurrentSessionId(sessionId);
    }
    
    const loadingMessageId = Date.now() + 1;
    setMessages(prev => [...prev, { text: '', sender: 'assistant', isLoading: true, timestamp: new Date(), id: loadingMessageId }]);

    // User message will be saved automatically by backend in /api/chat-stream

    try {
        const sheetData = (isSheetLoaded && isSheetSelected) ? 
          await getSheetData(extractSpreadsheetId(googleSheetsUrl), selectedSheetName) : null;
        
        // We need to get the spreadsheetId here to send it
        const spreadsheetId = isSheetLoaded ? extractSpreadsheetId(googleSheetsUrl) : null;

        const payload = {
          question: currentQuestion,
          sheetData: sheetData,
          extractedPdfData: extractedPdf ? extractedPdf.data : null,
          extractedExcelData: extractedExcel ? extractedExcel.data : null,
          isAgentMode: isAgentMode,
          conversationHistory: messages.filter(m => !m.isLoading).map(m => ({
              role: m.sender === 'user' ? 'user' : 'model',
              parts: [{ text: m.text }]
          })),
          spreadsheetId: spreadsheetId,
          accessToken: accessToken,
          sessionId: sessionId,  // Include session ID
          sheetsMetadata: sheetsMetadata,       // ◊ï◊ì◊ê ◊©◊î◊û◊©◊™◊†◊î ◊î◊ñ◊î ◊ß◊ô◊ô◊ù ◊ï◊û◊õ◊ô◊ú ◊ê◊™ ◊î◊û◊ô◊ì◊¢
          selectedSheetName: selectedSheetName  // ◊ï◊ì◊ê ◊©◊î◊û◊©◊™◊†◊î ◊î◊ñ◊î ◊ß◊ô◊ô◊ù ◊ï◊û◊õ◊ô◊ú ◊ê◊™ ◊©◊ù ◊î◊í◊ô◊ú◊ô◊ï◊ü
        };
    
        
        const response = await fetch(`${API_URL}/api/chat-stream`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            credentials: 'include', // This is crucial for session cookies!
            body: JSON.stringify(payload),
        });

        if (!response.ok || !response.body) {
            const errorText = await response.text();
            throw new Error(`Server error: ${response.status} ${errorText}`);
        }

        const reader = response.body.getReader();
        const decoder = new TextDecoder();
        let accumulatedResponse = '';

        while (true) {
            const { value, done } = await reader.read();
            if (done) break;
            
            accumulatedResponse += decoder.decode(value, { stream: true });

            const currentResponse = accumulatedResponse;
            setMessages(prev => prev.map(m => {
                if (m.id === loadingMessageId) {
                    // Show the accumulated response directly as text
                    return { ...m, text: currentResponse, isLoading: true };
                }
                return m;
            }));
        }

        // Final response processing - just show the accumulated text
        setMessages(prev => prev.map(m =>
            m.id === loadingMessageId
            ? { ...m, text: accumulatedResponse, isLoading: false }
            : m
        ));

        // Assistant response is automatically saved by backend in /api/chat-stream
        
                 // ◊ê◊ù ◊î◊ô◊î ◊ñ◊î ◊§◊ß◊ï◊ì◊™ undo/redo, ◊®◊¢◊†◊ü ◊ê◊™ ◊î◊í◊ú◊ô◊ï◊ü
         const questionLower = currentQuestion.toLowerCase().trim();
         const undoRedoKeywords = ['◊ë◊ò◊ú', 'undo', '◊©◊ó◊ñ◊®', 'redo', '◊ë◊ò◊ú ◊§◊¢◊ï◊ú◊î', '◊©◊ó◊ñ◊® ◊§◊¢◊ï◊ú◊î'];
         if (undoRedoKeywords.some(keyword => questionLower.includes(keyword))) {
             // ◊®◊¢◊†◊ï◊ü ◊ó◊ú◊ß ◊©◊ú ◊î◊í◊ú◊ô◊ï◊ü
             if (googleSheetsUrl && selectedSheetName) {
                 setTimeout(() => {
                     const iframe = document.querySelector('iframe[title="Google Sheets"]');
                     if (iframe) {
                         iframe.src = iframe.src;
                     }
                 }, 800); // ◊î◊©◊î◊ô◊î ◊û◊¢◊ò ◊ô◊ï◊™◊® ◊ê◊®◊ï◊õ◊î ◊¢◊ë◊ï◊® ◊§◊ß◊ï◊ì◊ï◊™ ◊¶'◊ê◊ò
             }
         }

    } catch (error) {
        console.error('Error in sendMessage:', error);
        setMessages(prev => prev.map(m => 
            m.id === loadingMessageId 
            ? { ...m, text: `◊ê◊ô◊®◊¢◊î ◊©◊í◊ô◊ê◊î: ${error.message}`, isLoading: false, isError: true } 
            : m
        ));
    } finally {
        setIsStreamingResponse(false);
        // ◊ë◊ì◊ï◊ß ◊°◊ò◊ò◊ï◊° Undo/Redo ◊ï◊™◊ê◊ô◊ù ◊ô◊®◊ï◊ß◊ô◊ù ◊ê◊ó◊®◊ô ◊õ◊ú ◊î◊ï◊ì◊¢◊î
        console.log('üìã Message completed, checking status...', { isAgentMode, currentSessionId });
        
        // For Agent Mode, force immediate update and multiple checks
        if (isAgentMode) {
          console.log('ü§ñ Agent Mode - immediate pending changes check');
          console.log('üî• Current state before updates:', { hasGreenCells, changesCount, currentSessionId });
          
          // Force immediate state update for Agent Mode
          console.log('üî• FORCE: Setting buttons visible immediately for Agent Mode');
          setHasGreenCells(true);
          setChangesCount(1); // Assume at least 1 change
          
          // Log state after force update
          setTimeout(() => {
            console.log('üî• State after force update:', { hasGreenCells, changesCount });
          }, 100);
          
          // Then verify with actual data - multiple attempts
          console.log('üîç Starting immediate checkForGreenCells...');
          checkForGreenCells();
          
          setTimeout(() => {
            console.log('‚è∞ Agent Mode - delayed status checks (1s)...');
            checkUndoRedoStatus();
            checkForGreenCells();
          }, 1000);
          
          setTimeout(() => {
            console.log('‚è∞ Agent Mode - delayed status checks (2.5s)...');
            checkUndoRedoStatus();
            checkForGreenCells();
          }, 2500); // ◊ô◊ï◊™◊® ◊ñ◊û◊ü ◊¢◊ë◊ï◊® Agent Mode ◊õ◊ì◊ô ◊ú◊ï◊ï◊ì◊ê ◊©◊îPendingChanges ◊†◊©◊û◊®◊ï
          
          // Additional check after even more time
          setTimeout(() => {
            console.log('‚è∞ Agent Mode - final delayed check (4s)...');
            checkForGreenCells();
          }, 4000);
          
          // Force check every 2 seconds for debugging
          const debugInterval = setInterval(() => {
            console.log('üîÑ Debug interval check - calling checkForGreenCells');
            checkForGreenCells();
          }, 2000);
          
          // Stop debug interval after 15 seconds
          setTimeout(() => {
            clearInterval(debugInterval);
            console.log('üõë Stopped debug interval');
          }, 15000);
          
        } else {
          setTimeout(() => {
            console.log('‚è∞ Running status checks...');
            checkUndoRedoStatus();
            checkForGreenCells();
          }, 1000); // ◊î◊©◊î◊ô◊î ◊ß◊¶◊®◊î ◊õ◊ì◊ô ◊ú◊ï◊ï◊ì◊ê ◊©◊î◊ë◊ß-◊ê◊†◊ì ◊°◊ô◊ô◊ù
        }
    }
  };

  const saveAllChanges = () => {
    setMessages(prev => [...prev, { text: `üíæ ◊õ◊ú ◊î◊©◊ô◊†◊ï◊ô◊ô◊ù ◊†◊©◊û◊®◊ï ◊ú◊í◊ô◊ú◊ô◊ï◊ü ${fileName}`, sender: 'system', timestamp: new Date(), id: Date.now() }]);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      

      
      <Box sx={{ display: 'flex', height: '100vh', bgcolor: 'background.default', overflow: 'hidden' }}>
        <Box sx={{ position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, background: `radial-gradient(circle at 20% 30%, rgba(139, 92, 246, 0.08) 0%, transparent 60%), radial-gradient(circle at 80% 70%, rgba(99, 102, 241, 0.06) 0%, transparent 60%)`, pointerEvents: 'none', zIndex: 0 }} />
        {!isMobile && (
          <Drawer variant="permanent" sx={{ width: drawerWidth, flexShrink: 0, zIndex: 1, '& .MuiDrawer-paper': { width: drawerWidth, boxSizing: 'border-box', bgcolor: 'rgba(10, 10, 10, 0.98)', borderRight: '1px solid rgba(139, 92, 246, 0.4)', backdropFilter: 'blur(20px)' }}}>
            <Box sx={{ p: 3 }}>
              <Typography variant="h6" sx={{ background: 'linear-gradient(135deg, #8B5CF6 0%, #6366F1 100%)', backgroundClip: 'text', WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent', fontWeight: 700 }}>TLV500</Typography>
              <Typography variant="body2" color="text.secondary">AI Assistant</Typography>
            </Box>
            <Divider sx={{ borderColor: 'rgba(139, 92, 246, 0.4)' }} />
            <Box sx={{ p: 3 }}>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, mb: 2 }}>
                <Avatar
                  src={user?.profilePicture}
                  sx={{ width: 45, height: 45, background: 'linear-gradient(135deg, #8B5CF6 0%, #6366F1 100%)' }}
                >
                  {user?.name ? user.name.charAt(0) : <Person />}
                </Avatar>
                <Box sx={{ flex: 1 }}>
