  // 📊 NEW: Check for pending changes instead of green cells
  const checkForGreenCells = async () => {
    if (!googleSheetsUrl || !selectedSheetName || isCheckingGreenCells) {
      console.log('⚠️ Skipping green cells check - missing requirements or already checking');
      console.log({ googleSheetsUrl: !!googleSheetsUrl, selectedSheetName, isCheckingGreenCells });
      return;
    }
    
    // 🔍 Critical check: Make sure we have a session ID
    if (!currentSessionId) {
      console.log('⚠️ No session ID available, skipping pending changes check');
      setHasGreenCells(false);
      setChangesCount(0);
      return;
    }
    
    setIsCheckingGreenCells(true);
    try {
      console.log(`🔍 Checking pending changes for session: ${currentSessionId}`);
      console.log(`📍 Sheet: ${selectedSheetName}, URL: ${googleSheetsUrl}`);
      
      // Use the action status endpoint which now counts pending changes
      const response = await fetch(`${API_URL}/api/action/status/${currentSessionId}`, {
