        const pendingChanges = result.changesCount || 0;
        
        console.log(`🔍 Before state update: hasGreenCells=${hasGreenCells}, changesCount=${changesCount}`);
        console.log(`🔄 Setting hasGreenCells to: ${pendingChanges > 0}, changesCount to: ${pendingChanges}`);
        
        setHasGreenCells(pendingChanges > 0);
        setChangesCount(pendingChanges);
        
        // Force a re-render check immediately after state update
        setTimeout(() => {
          console.log(`✅ After state update: hasGreenCells=${pendingChanges > 0}, changesCount=${pendingChanges}, buttons should be visible: ${pendingChanges > 0}`);
          // Debug the button visibility condition
          const shouldShow = isSheetLoaded && isSheetSelected && (pendingChanges > 0);
