import React, { useState, useEffect, useRef } from 'react';

// API URL configuration
const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';
import {
  Box,
  Drawer,
  Typography,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Paper,
  TextField,
  Button,
  Avatar,
  Divider,
  Chip,
  Fab,
  useMediaQuery,
  useTheme,
  CircularProgress,
  Alert,
  Snackbar,
  CssBaseline,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Switch,
  FormControlLabel
} from '@mui/material';
import {
  Send,
  Person,
  Description,
  TableChart,
  Save
} from '@mui/icons-material';
// No longer needed from frontend: import { GoogleGenerativeAI } from '@google/generative-ai';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import ChatHistory from './components/ChatHistory';
import './App.css';

const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: { main: '#8B5CF6' },
    secondary: { main: '#6366F1' },
    background: { default: '#000000', paper: '#0A0A0A' },
    text: { primary: '#FFFFFF', secondary: '#E5E7EB' },
    success: { main: '#10B981' },
    error: { main: '#EF4444' }
  },
  typography: {
    fontFamily: '"Inter", "Roboto", "Helvetica", "Arial", sans-serif',
    h4: { fontWeight: 700 },
    h6: { fontWeight: 600 }
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: { borderRadius: 12, textTransform: 'none', fontWeight: 600, padding: '10px 20px' },
        contained: {
          boxShadow: '0 4px 20px rgba(139, 92, 246, 0.4)',
          background: 'linear-gradient(135deg, #8B5CF6 0%, #6366F1 100%)',
          '&:hover': {
            transform: 'translateY(-1px)',
            boxShadow: '0 6px 30px rgba(139, 92, 246, 0.6)',
            background: 'linear-gradient(135deg, #A78BFA 0%, #818CF8 100%)',
          }
        },
      },
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          background: 'rgba(20, 20, 20, 0.95)',
          backdropFilter: 'blur(20px)',
          border: '1px solid rgba(139, 92, 246, 0.3)',
          borderRadius: 16,
        },
      },
    },
  },
});

const drawerWidth = 280;

function App() {
  const muiTheme = useTheme();
  const isMobile = useMediaQuery(muiTheme.breakpoints.down('md'));
  
  const [messages, setMessages] = useState([
    { text: '×©×œ×•×! ×× ×™ TLV500 AI Assistant. ×”×ª×—×‘×¨ ×œ×—×©×‘×•×Ÿ Google ×•×‘×—×¨ ×’×™×œ×™×•×Ÿ ×›×“×™ ×œ×”×ª×—×™×œ.', sender: 'assistant', timestamp: new Date(), id: Date.now() }
  ]);
  const [question, setQuestion] = useState('');
  const [fileName, setFileName] = useState('');
  const [googleSheetsUrl, setGoogleSheetsUrl] = useState('');
  const [lastInternalNotes, setLastInternalNotes] = useState("");
  const [changesCount, setChangesCount] = useState(0);
  const [isSheetLoaded, setIsSheetLoaded] = useState(false);

  const [isPickerReady, setIsPickerReady] = useState(false);
  const [accessToken, setAccessToken] = useState('');
  const [sheetsMetadata, setSheetsMetadata] = useState('');
  const [authError, setAuthError] = useState('');
  const [showSnackbar, setShowSnackbar] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState('');
  const [availableSheets, setAvailableSheets] = useState([]);
  const [selectedSheetName, setSelectedSheetName] = useState('');
  const [isLoadingSheets, setIsLoadingSheets] = useState(false);
  const [extractedPdf, setExtractedPdf] = useState(null);
  const [isExtractingData, setIsExtractingData] = useState(false);
  const [extractedExcel, setExtractedExcel] = useState(null);
  const [isExtractingExcel, setIsExtractingExcel] = useState(false);
  const [isAgentMode, setIsAgentMode] = useState(false);
  const [isStreamingResponse, setIsStreamingResponse] = useState(false);
  
  // Chat History Management
  const [currentSessionId, setCurrentSessionId] = useState(null);
  const [user, setUser] = useState(null);
  
  const chatEndRef = useRef(null);


  useEffect(() => {
    // Load saved access token ONLY - no chat messages from localStorage
    const savedToken = localStorage.getItem('accessToken');
    if (savedToken) {
      setAccessToken(savedToken);
    }

    // Only load PDF/Excel data from localStorage (temporary files, not chat history)
    const savedPdfJson = localStorage.getItem('extractedPdf');
    if (savedPdfJson) {
      try {
        const savedPdf = JSON.parse(savedPdfJson);
        if (savedPdf && savedPdf.name && savedPdf.data) {
          setExtractedPdf(savedPdf);
          setMessages(prev => [...prev, { text: `ðŸ“„ × ×ª×•× ×™× ×§×•×“×ž×™× ×©×—×•×œ×¦×• ×ž-PDF "${savedPdf.name}" × ×˜×¢× ×•.`, sender: 'system', timestamp: new Date(), id: Date.now() }]);
        }
      } catch (e) {
        console.error('Failed to parse extracted PDF data from localStorage', e);
        localStorage.removeItem('extractedPdf');
      }
    }

    const savedExcelJson = localStorage.getItem('extractedExcel');
    if (savedExcelJson) {
        try {
            const savedExcel = JSON.parse(savedExcelJson);
            if (savedExcel && savedExcel.name && savedExcel.data) {
                setExtractedExcel(savedExcel);
                setMessages(prev => [...prev, { text: `ðŸ“Š × ×ª×•× ×™× ×§×•×“×ž×™× ×©×—×•×œ×¦×• ×ž-Excel "${savedExcel.name}" × ×˜×¢× ×•.`, sender: 'system', timestamp: new Date(), id: Date.now() }]);
            }
        } catch (e) {
            console.error('Failed to parse extracted Excel data from localStorage', e);
            localStorage.removeItem('extractedExcel');
        }
    }
  }, []);

  // Fetch user info when access token is available
  useEffect(() => {
    if (accessToken) {
      fetchUserInfo();
    }
  }, [accessToken]);

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const token = params.get('token');
    if (token) {
      setAccessToken(token);
      localStorage.setItem('accessToken', token);
      setSnackbarMessage('×”×ª×—×‘×¨×ª ×‘×”×¦×œ×—×”!');
      setShowSnackbar(true);
      window.history.replaceState({}, document.title, "/");
    } else {
      // Check if user is already authenticated
      checkAuthStatus();
    }
  }, []);

  const checkAuthStatus = async () => {
    try {
      const response = await fetch(`${API_URL}/api/user`, {
        credentials: 'include'
      });
      
      if (response.ok) {
        const data = await response.json();
        setUser(data.user);
        if (data.accessToken) {
          setAccessToken(data.accessToken);
          localStorage.setItem('accessToken', data.accessToken);
        }
        setSnackbarMessage('×‘×¨×•×š ×”×‘× ×‘×—×–×¨×”!');
        setShowSnackbar(true);
      }
    } catch (error) {
      console.log('Not authenticated:', error);
      // User is not authenticated, show login button
    }
  };

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  useEffect(() => {
    const script = document.createElement('script');
    script.src = 'https://apis.google.com/js/api.js';
    script.onload = () => window.gapi.load('picker', { 'callback': () => setIsPickerReady(true) });
    document.head.appendChild(script);
  }, []);

  const fetchUserInfo = async () => {
    try {
      const response = await fetch(`${API_URL}/api/user`, {
        credentials: 'include'
      });
      
      if (response.ok) {
        const data = await response.json();
        setUser(data.user);
      }
    } catch (error) {
      console.error('Error fetching user info:', error);
    }
  };

  const handleSessionSelect = async (sessionId) => {
    if (!sessionId) {
      setCurrentSessionId(null);
      setMessages([{ text: '×©×œ×•×! ×× ×™ TLV500 AI Assistant. ×”×ª×—×‘×¨ ×œ×—×©×‘×•×Ÿ Google ×•×‘×—×¨ ×’×™×œ×™×•×Ÿ ×›×“×™ ×œ×”×ª×—×™×œ.', sender: 'assistant', timestamp: new Date(), id: Date.now() }]);
      return;
    }

    setCurrentSessionId(sessionId);
    
    try {
      const response = await fetch(`http://localhost:3001/api/chat/session/${sessionId}`, {
        credentials: 'include'
      });
      
      if (response.ok) {
        const chatHistory = await response.json();
        const loadedMessages = chatHistory.messages.map(msg => ({
          text: msg.content,
          sender: msg.role === 'user' ? 'user' : 'assistant',
          timestamp: new Date(msg.timestamp),
          id: msg._id || Date.now() + Math.random()
        }));
        
        setMessages(loadedMessages);
        // Chat history loaded
      }
    } catch (error) {
      console.error('Error loading chat session:', error);
      setSnackbarMessage('×©×’×™××” ×‘×˜×¢×™× ×ª ×”×©×™×—×”');
      setShowSnackbar(true);
    }
  };

  const generateSessionId = () => {
    return 'session_' + Date.now() + '_' + Math.random().toString(36).substr(2, 9);
  };

  function columnIndexToLetter(colIndex) {
    let temp, letter = '';
    let column = colIndex + 1;
    while (column > 0) {
      temp = (column - 1) % 26;
      letter = String.fromCharCode(temp + 65) + letter;
      column = (column - temp - 1) / 26;
    }
    return letter;
  }

  const renderMessageText = (text) => {
    if (typeof text !== 'string') return text;

    // Only handle code between backticks, leave everything else as normal text
    const parts = text.split('`');
    
    if (parts.length === 1) {
      // No backticks, return as normal text
      return text;
    }
    
    return parts.map((part, index) => {
      if (index % 2 === 1) {
        // This is code between backticks
        return (
          <Chip
            key={index}
            label={part}
            size="small"
            sx={{
              direction: 'ltr',
              backgroundColor: 'rgba(0,0,0,0.6)',
              color: '#E5E7EB',
              fontFamily: '"Fira Code", "Courier New", monospace',
              height: 'auto',
              mx: 0.5,
              '& .MuiChip-label': {
                padding: '4px 8px',
                fontSize: '0.85em',
              },
            }}
          />
        );
      } else {
        // Regular text
        return part;
      }
    });
  };

  function processSheetData(values) {
    const mappedData = {};
    if (!Array.isArray(values)) return mappedData;
    values.forEach((row, rowIndex) => {
      if (row && row.length > 0) {
        row.forEach((cellValue, colIndex) => {
          if (cellValue !== null && cellValue !== '') {
            const cellAddress = columnIndexToLetter(colIndex) + (rowIndex + 1);
            mappedData[cellAddress] = cellValue;
          }
        });
      }
    });
    return mappedData;
  }
  
  const makeGoogleSheetsAPICall = async (spreadsheetId, range, values = null, method = 'GET') => {
    if (!accessToken) throw new Error('No access token available. Please log in.');
    const baseUrl = `https://sheets.googleapis.com/v4/spreadsheets/${spreadsheetId}`;
    try {
      let url, options;
      if (method === 'GET') {
        url = `${baseUrl}/values/${range}`;
        options = { method, headers: { 'Authorization': `Bearer ${accessToken}`, 'Content-Type': 'application/json' }};
      } else {
        url = `${baseUrl}/values/${range}?valueInputOption=RAW`;
        options = { method, headers: { 'Authorization': `Bearer ${accessToken}`, 'Content-Type': 'application/json' }, body: JSON.stringify({ values }) };
      }
      const response = await fetch(url, options);
      const data = await response.json();
      if (!response.ok) throw new Error(data.error?.message || 'API call failed');
      return data;
    } catch (error) {
      console.error('Google Sheets API error:', error);
      throw error;
    }
  };

  const extractSpreadsheetId = (url) => {
    const match = url.match(/\/spreadsheets\/d\/([a-zA-Z0-9-_]+)/);
    return match ? match[1] : null;
  };
  
  const getSpreadsheetMetadata = async (spreadsheetId) => {
    if (!accessToken) return [];
    try {
      const url = `https://sheets.googleapis.com/v4/spreadsheets/${spreadsheetId}`;
      const response = await fetch(url, { headers: { 'Authorization': `Bearer ${accessToken}` } });
      if (!response.ok) throw new Error('Failed to fetch spreadsheet metadata');
      const metadata = await response.json();
      return metadata.sheets.map(sheet => ({ name: sheet.properties.title, id: sheet.properties.sheetId }));
    } catch (error) {
      console.error('Error fetching spreadsheet metadata:', error);
      return [];
    }
  };

  const handleFileUpload = async (event, fileType) => {
    const file = event.target.files[0];
    if (!file) return;

    if (file.type === 'application/pdf') {
        // Clean all previous PDF-related state
        setExtractedPdf(null);
        localStorage.removeItem('extractedPdf');
        setIsExtractingData(true);
        
        // Remove any previous PDF-related messages
        setMessages(prev => prev.filter(msg => 
            !msg.text.includes('× ×ª×•× ×™× ×—×•×œ×¦×•') && 
            !msg.text.includes('×©×’×™××” ×‘×—×™×œ×•×¥') &&
            !msg.text.includes('× ×ª×•× ×™ ×”-PDF × ×•×§×•')
        ));
        
        setMessages(prev => [...prev, { text: `â³ ×ž×—×œ×¥ × ×ª×•× ×™× ×ž×§×•×‘×¥ "${file.name}"...`, sender: 'system', timestamp: new Date(), id: Date.now() }]);
        
        const formData = new FormData();
        formData.append('pdf', file);

        fetch('http://localhost:3001/api/extract-pdf-data', { 
            method: 'POST', 
            body: formData 
        })
        .then(response => {
            if (!response.ok) {
                return response.json().then(err => { throw new Error(err.error || 'Failed to extract data') });
            }
            return response.json();
        })
        .then(result => {
            const pdfInfo = { name: file.name, data: result };
            setExtractedPdf(pdfInfo);
            localStorage.setItem('extractedPdf', JSON.stringify(pdfInfo));
            setMessages(prev => prev.filter(msg => !msg.text.includes('â³ ×ž×—×œ×¥ × ×ª×•× ×™×')));
            setMessages(prev => [...prev, { text: `âœ… × ×ª×•× ×™× ×—×•×œ×¦×• ×‘×”×¦×œ×—×” ×ž-"${file.name}". ×›×¢×ª × ×™×ª×Ÿ ×œ×©××•×œ ×©××œ×•×ª ×¢×œ ×”×§×•×‘×¥.`, sender: 'system', timestamp: new Date(), id: Date.now() }]);
        })
        .catch(error => {
            console.error('PDF data extraction failed:', error);
            setMessages(prev => prev.filter(msg => !msg.text.includes('â³ ×ž×—×œ×¥ × ×ª×•× ×™×')));
            setMessages(prev => [...prev, { text: `âŒ ×©×’×™××” ×‘×—×™×œ×•×¥ × ×ª×•× ×™× ×ž-"${file.name}": ${error.message}`, sender: 'system', timestamp: new Date(), id: Date.now() }]);
        })
        .finally(() => {
            setIsExtractingData(false);
        });
    } else if (fileType === 'Excel') {
        setExtractedExcel(null);
        localStorage.removeItem('extractedExcel');
        setIsExtractingExcel(true);
        
        setMessages(prev => prev.filter(msg => 
            !msg.text.includes('×—×•×œ×¦×• ×ž-Excel') && 
            !msg.text.includes('×©×’×™××” ×‘×—×™×œ×•×¥ Excel') &&
            !msg.text.includes('× ×ª×•× ×™ ×”-Excel × ×•×§×•')
        ));
        
        setMessages(prev => [...prev, { text: `â³ ×ž×—×œ×¥ × ×ª×•× ×™× ×ž×§×•×‘×¥ Excel "${file.name}"...`, sender: 'system', timestamp: new Date(), id: Date.now() }]);
        
        const formData = new FormData();
        formData.append('excel', file);

        fetch('http://localhost:3001/api/extract-excel-data', { 
            method: 'POST', 
            body: formData 
        })
        .then(response => {
            if (!response.ok) {
                return response.json().then(err => { throw new Error(err.error || 'Failed to extract Excel data') });
            }
            return response.json();
        })
        .then(result => {
            const excelInfo = { name: file.name, data: result.data };
            setExtractedExcel(excelInfo);
            localStorage.setItem('extractedExcel', JSON.stringify(excelInfo));
            setMessages(prev => prev.filter(msg => !msg.text.includes('â³ ×ž×—×œ×¥ × ×ª×•× ×™× ×ž×§×•×‘×¥ Excel')));
            setMessages(prev => [...prev, { text: `âœ… × ×ª×•× ×™× ×—×•×œ×¦×• ×‘×”×¦×œ×—×” ×ž-Excel "${file.name}".`, sender: 'system', timestamp: new Date(), id: Date.now() }]);
        })
        .catch(error => {
            console.error('Excel data extraction failed:', error);
            setMessages(prev => prev.filter(msg => !msg.text.includes('â³ ×ž×—×œ×¥ × ×ª×•× ×™× ×ž×§×•×‘×¥ Excel')));
            setMessages(prev => [...prev, { text: `âŒ ×©×’×™××” ×‘×—×™×œ×•×¥ × ×ª×•× ×™ Excel: ${error.message}`, sender: 'system', timestamp: new Date(), id: Date.now() }]);
        })
        .finally(() => {
            setIsExtractingExcel(false);
        });
    }
  };

  const loadAvailableSheets = async (spreadsheetId) => {
    setIsLoadingSheets(true);
    try {
      const sheets = await getSpreadsheetMetadata(spreadsheetId);
      setAvailableSheets(sheets);
      if (sheets.length > 0) {
        setSelectedSheetName(sheets[0].name);
        setSheetsMetadata(sheets);
        await getSheetData(spreadsheetId, sheets[0].name);
        setMessages(prev => [...prev, { text: `ðŸ” ×’×œ×™×•×Ÿ ×¤×¢×™×œ: ${sheets[0]?.name || '×œ× ×™×“×•×¢'}`, sender: 'system', timestamp: new Date(), id: Date.now() }]);
      }
    } finally {
      setIsLoadingSheets(false);
    }
  };

  const handleGoogleSheetsUrl = async (url) => {
    setGoogleSheetsUrl(url);
    setIsSheetLoaded(true);
    const spreadsheetId = extractSpreadsheetId(url);
    if (spreadsheetId && accessToken) await loadAvailableSheets(spreadsheetId);
  };
  
  const handleSheetChange = async (newSheetName) => {
    if (newSheetName === selectedSheetName) return;
    setSelectedSheetName(newSheetName);
    const spreadsheetId = extractSpreadsheetId(googleSheetsUrl);
    if (spreadsheetId) {
      await getSheetData(spreadsheetId, newSheetName);
      setMessages(prev => [...prev, { text: `ðŸ“‹ ×”×•×—×œ×£ ×œ×’×œ×™×•×Ÿ: ${newSheetName}`, sender: 'system', timestamp: new Date(), id: Date.now() }]);
    }
  };

  const pickerCallback = (data) => {
