  const createPicker = () => {
    const developerKey = process.env.REACT_APP_GEMINI_API_KEY;
    const clientId = process.env.REACT_APP_GOOGLE_CLIENT_ID;
    if (!clientId || !developerKey) {
      setAuthError('×ž×¤×ª×—×•×ª API ××™× × ×ž×•×’×“×¨×™× ×›×¨××•×™.');
