              <Button variant="contained" startIcon={<Save />} onClick={saveAllChanges} color="primary" size="small">×©×ž×•×¨ ×”×›×œ</Button>
            </Box>
            <Paper sx={{ flexGrow: 1, p: 2, display: 'flex', flexDirection: 'column', overflow: 'hidden', background: 'rgba(15, 15, 15, 0.98)', border: '1px solid rgba(139, 92, 246, 0.25)' }}>
