                      <Button size="small" onClick={() => { setExtractedPdf(null); localStorage.removeItem('extractedPdf'); setMessages(prev => [...prev, { text: 'ðŸ“„ × ×ª×•× ×™ ×”-PDF × ×•×§×• ×ž×”×”×§×©×¨.', sender: 'system', timestamp: new Date(), id: Date.now() }]); }} sx={{ color: 'error.main', minWidth: 'auto', p: 0.5 }}>âœ•</Button>
                    </Box>
