                    </Box>
                  )}
                </Box>
              </Box>
              <Box sx={{ flexGrow: 1, overflow: 'auto', p: 2 }}>
                {messages.map((msg, index) => (
                  <Box key={msg.id || index} sx={{ mb: 2 }} className="chat-message">
                    <Box sx={{ display: 'flex', justifyContent: msg.sender === 'user' ? 'flex-end' : 'flex-start', mb: 1 }}>
                      <Paper sx={{ 
                        p: 1.5, 
                        maxWidth: '85%', 
                        background: msg.sender === 'user' ? 'linear-gradient(135deg, #8B5CF6 0%, #6366F1 100%)' : 'rgba(35, 35, 35, 0.9)', 
                        color: 'white', 
                        borderRadius: 3
                      }}>
                        {msg.isLoading ? <CircularProgress size={16} color="inherit" /> : <Typography variant="body2" sx={{ whiteSpace: 'pre-wrap' }}>{msg.text}</Typography>}
                      </Paper>
                    </Box>
                    <Typography variant="caption" color="text.secondary" sx={{ display: 'block', textAlign: msg.sender === 'user' ? 'right' : 'left', px: 1 }}>
                      {msg.timestamp.toLocaleTimeString('he-IL', { hour: '2-digit', minute: '2-digit' })}
                    </Typography>
                  </Box>
                ))}
                <div ref={chatEndRef} />
              </Box>
              <Box sx={{ p: 2, borderTop: '1px solid rgba(139, 92, 246, 0.4)' }}>
                <Box sx={{ display: 'flex', gap: 1 }}>
                  <TextField 
                    fullWidth 
                    variant="outlined" 
                    placeholder="×©××œ ××•×ª×™ ×›×œ ×“×‘×¨... (Shift+Enter ×œ×©×•×¨×” ×—×“×©×”)" 
                    value={question} 
                    onChange={(e) => setQuestion(e.target.value)} 
                    onKeyPress={(e) => {
                      if (e.key === 'Enter' && !e.shiftKey) {
                        e.preventDefault(); // Prevents adding a new line
                        sendMessage();
                      }
                    }} 
                    multiline
                    minRows={2}
                    maxRows={5}
                    sx={{
                      direction: 'rtl',
                      '& .MuiOutlinedInput-root': {
                        borderRadius: 3,
                        direction: 'rtl',
                        '& textarea': {
                          textAlign: 'right',
                          direction: 'rtl',
                        },
                        '& input': {
                          textAlign: 'right',
                          direction: 'rtl',
                        },
                      },
                      '& .MuiInputLabel-root': {
                        right: 'auto',
                        left: 0,
                        transformOrigin: 'top right',
                        direction: 'rtl',
                      },
                      '& .MuiFormLabel-root': {
                        textAlign: 'right',
                        direction: 'rtl',
                      },
                    }}
                  />
                  <Fab color="primary" onClick={sendMessage} size="small"><Send /></Fab>
                </Box>
                <FormControlLabel
                  control={
                    <Switch
                      checked={isAgentMode}
                      onChange={(e) => setIsAgentMode(e.target.checked)}
                      disabled={!googleSheetsUrl}
                    />
                  }
                  label="Agent Mode: Allow Sheet Editing"
                  sx={{ 
                    color: !googleSheetsUrl ? 'text.disabled' : 'text.secondary', 
                    mt: 1, 
                    mr: 0 
                  }}
                />
              </Box>
            </Paper>
          </Box>
        </Box>
        <Snackbar open={showSnackbar} autoHideDuration={6000} onClose={() => setShowSnackbar(false)} anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }} sx={{ bottom: { xs: 90, sm: 24 } }}>
          <Alert onClose={() => setShowSnackbar(false)} severity={authError ? "error" : "success"} sx={{ width: '100%', bgcolor: '#2E2E2E', color: 'white' }}>
            {snackbarMessage || authError}
          </Alert>
        </Snackbar>
      </Box>
    </ThemeProvider>
  );
}

export default App;
